BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "TBUSERBANK" (
	"ID_USERBANK"	INTEGER PRIMARY KEY AUTOINCREMENT,
	"ID_USER"	INTEGER,
	"ID_BANK"	INTEGER,
	"SALDO_INICIAL"	NUMERIC(15 , 2),
	"PATH"	VARCHAR(100),
	"APELIDO"	VARCHAR(20)
);
CREATE TABLE IF NOT EXISTS "TBTRANSACAO" (
	"Id"	INTEGER PRIMARY KEY AUTOINCREMENT,
	"ID_USER"	INTEGER,
	"Tipo"	VARCHAR(20),
	"DataTransacao"	DATE,
	"Favorecido"	VARCHAR(100),
	"Categoria"	VARCHAR(100),
	"SubCategoria"	VARCHAR(100),
	"Valor"	REAL(15 , 2),
	"CENTRODECUSTO"	VARCHAR(100),
	"DataCriacao"	datetime DEFAULT CURRENT_TIMESTAMP,
	"ALTERADO"	VARCHAR(3) DEFAULT 'NAO',
	"ID_BANKING"	INTEGER,
	"FORECAST"	INTEGER DEFAULT 0,
	"ID_USERBANK"	INTEGER,
	"MODO"	CHAR(1)
);
CREATE TABLE IF NOT EXISTS "TBBANKING" (
	"ID_BANKING"	INTEGER PRIMARY KEY AUTOINCREMENT,
	"NAME"	VARCHAR(80),
	"ACCOUNT_TYPE"	VARCHAR(4)
);
CREATE TABLE IF NOT EXISTS "TBUSER" (
	"ID_USER"	INTEGER PRIMARY KEY AUTOINCREMENT,
	"FULLNAME"	TEXT,
	"EMAIL"	TEXT,
	"PWD"	TEXT,
	"COMPUTERNAME"	TEXT,
	"PHONE_NUMBER"	TEXT,
	"ACTIVE"	INTEGER,
	"ID_LANGUAGE"	INTEGER,
	"CREATE_DATE"	TEXT DEFAULT CURRENT_TIMESTAMP,
	"ID_USERPAI"	INTEGER
);
CREATE TABLE IF NOT EXISTS "CENTRODECUSTO" (
	"ID_CentrodeCusto"	INTEGER PRIMARY KEY AUTOINCREMENT,
	"CentroDeCusto"	VARCHAR(100),
	"ID_USER"	INTEGER,
	"ID_LANGUAGE"	INTEGER,
	"GRUPO"	INTEGER
);
CREATE TABLE IF NOT EXISTS "TBCATEGORIA" (
	"ID_CATEGORIA"	INTEGER PRIMARY KEY AUTOINCREMENT,
	"FAVORECIDO"	VARCHAR(100),
	"CATEGORIA"	VARCHAR(100),
	"ID_CentrodeCusto"	INTEGER,
	"ID_LANGUAGE"	INTEGER,
	CONSTRAINT "UQ_CATEGORIA" UNIQUE("FAVORECIDO","CATEGORIA","ID_LANGUAGE")
);
CREATE TABLE IF NOT EXISTS "TBMESSAGE" (
	"ID_TBMESSAGE"	INTEGER PRIMARY KEY AUTOINCREMENT,
	"ID_LANGUAGE"	INTEGER,
	"DESCRIPTION_TYPE"	TEXT,
	"DESCRIPTION_MESSAGE"	TEXT
);
CREATE TABLE IF NOT EXISTS "TBPERIODO" (
	"ID_PERIODO"	INTEGER PRIMARY KEY AUTOINCREMENT,
	"ANOATUAL"	TEXT,
	"ANOFUTURO"	TEXT
);
CREATE TABLE IF NOT EXISTS "TBPARAMETERS" (
	"ID_PARAMETERES"	INTEGER PRIMARY KEY AUTOINCREMENT,
	"DESCRIPTION_VALUE"	TEXT,
	"DESCRIPTION_CONTENT"	TEXT
);
CREATE TABLE IF NOT EXISTS "TBLANGUAGE" (
	"ID_LANGUAGE"	INTEGER PRIMARY KEY AUTOINCREMENT,
	"LANGUAGE_DESC"	TEXT
);
CREATE UNIQUE INDEX IF NOT EXISTS "UQ_USERBANKAP" ON "TBUSERBANK" (
	"ID_USER",
	"ID_BANK",
	"APELIDO"
);
COMMIT;
